mapper.delete:
delete from board 
where bno = #{bno}
and	  password = #{password}

mapper.select:
select count(*)
from  board 
where bno = #{bno}
and	  password = #{password}

mapper.update:
update	board
set 	title = #{title},
		content = #{content},
		ticket = #{ticket},
		type = #{type},
		stars = #{stars},
		rname = #{rname}
where 	bno = #{bno}
and		password = #{password}
