mapper.update:
update	board
set 	title = #{title},
		content = #{content}
where 	bno = #{bno}

mapper.select:

		select 
    			bno, title, content,
   				writer, regdate, type, stars
		from (
			select /*+INDEX_DESC(board pk_board_bno) */
    			rownum rn, bno, title, content,	writer, regdate, type, stars
			from board
			where rownum <= #{pageNum} * #{amount}
		)
		where rn > (#{pageNum} - 1) * #{amount}
